    /**
     * 递归遍历
     * @param array 集合
     * @param builder
     */
    private static void recursive(JSONArray array, DocumentBuilder builder, int level, Map<String,Object> pictureMap) throws Exception{
        if(array.size() > 0){
            level++;
            //超过五级的节点对应腾讯文档项目符号
            if (level >= 6) {
                level--;
                recursive6(array,builder,level,pictureMap);
            } else {
                for(int i=0;i<array.size();i++){
                    JSONObject node = array.getJSONObject(i);
                    if(node != null && node.containsKey("id")){
                        recursiveEndNode++;
                        //递归子元素
                        if(node.containsKey("children")){
                            JSONArray children = node.getJSONArray("children");
                            recursive(children,builder,level,pictureMap);
                        }
                    }
                }
            }
        }
    }

    /**
     * 递归遍历
     * @param array 集合
     * @param builder
     */
    private static void recursive6(JSONArray array, DocumentBuilder builder,int level,Map<String,Object> pictureMap) throws Exception{
        if(array.size() > 0){
            level++;
            for(int i=0;i<array.size();i++){
                JSONObject node = array.getJSONObject(i);
                if(node != null && node.containsKey("id")){
                    recursiveEndNode++;
                    //递归子元素
                    if(node.containsKey("children")){
                        JSONArray children = node.getJSONArray("children");
                        recursive6(children,builder,level,pictureMap);
                    }
                }
            }
        }
    }
